/* 检测两个label包围盒是否重叠 */
export function isBBoxIntersect(bboxA, bboxB) {
    if (bboxA.maxY < bboxB.minY || bboxB.maxY < bboxA.minY) {
        return false;
    }
    if (bboxA.maxX < bboxB.minX || bboxB.maxX < bboxA.minX) {
        return false;
